sys_script.script:
(function executeRule(current, previous /*null when async*/) {

try {
gs.log('Ravi Start');
var raw_data = current.u_raw_json;

var parsedData = JSON.parse(raw_data);

// get the header tags
for (i=0; i<parsedData.series.length; i++) {
	current.u_producer = parsedData.series[i].tags.Producer;
	current.u_alert_id = parsedData.series[i].tags.UUID;
	current.u_kpi = parsedData.series[i].tags.kpi_id;
	current.u_level = parsedData.series[i].tags.level;
	current.u_profile_id = parsedData.series[i].tags.profile_id;
	current.u_state = parsedData.series[i].tags.state;

	// get columns and values.
	columns = parsedData.series[i].columns;
	values = parsedData.series[i].values[0];
	for (j=0; j<columns.length; j++) {
       column = columns[j];
       value = values[j];
       current.u_column_values[column] = value;
    }	
}

 //current.update();

 gs.log('Ravi done');
}
catch (ex) {
	var message = ex.message;
}
})(current, previous);
